    <script>
    function toggleFaq(element) {
        const faqCard = element.parentElement;
        const faqAnswer = faqCard.querySelector(".faq-answer");
        const allFaqCards = document.querySelectorAll(".faq-card");
        allFaqCards.forEach((card) => {
            if (card !== faqCard && card.classList.contains("active")) {
                card.classList.remove("active");
                const answer = card.querySelector(".faq-answer");
                answer.style.maxHeight = null;
                answer.style.paddingTop = null;
                answer.style.paddingBottom = null;
                card.querySelector(".arrow").src = "./wp-content/uploads/caprome-cross-icon.png";
            }
        });
        if (faqCard.classList.contains("active")) {
            faqCard.classList.remove("active");
            faqAnswer.style.maxHeight = null;
            faqAnswer.style.paddingTop = null;
            faqAnswer.style.paddingBottom = null;
            faqCard.querySelector(".arrow").src = "./wp-content/uploads/caprome-cross-icon.png";
        } else {
            faqCard.classList.add("active");
            faqAnswer.style.maxHeight = faqAnswer.scrollHeight + "px";
            faqAnswer.style.paddingTop = "10px";
            faqAnswer.style.paddingBottom = "10px";
            faqCard.querySelector(".arrow").src = "./wp-content/uploads/caprome-dash-icon.png";
        }
    }
    </script>
</section>
